;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    T.mu = M.nu, T.nu = M.mu, T.tu = M.tu;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt;(T.tu)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(col = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; col &amp;lt;= M.nu; col++) num[col] = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(t = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; t &amp;lt;= M.tu; t++) ++num[M.data[t].j];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        cpot[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(col = &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;;col &amp;lt;= M.nu; ++col) cpot[col] = cpot[col - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + num[col - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt;(p = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; p &amp;lt;= M.tu; p++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            col = M.data[p].j;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            q = cpot[col];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            T.data[q].i = M.data[p].j;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            T.data[q].j = M.data[p].i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            T.data[q].e = M.data[p].e;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ++cpot[col];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
